import TodoItem from "../TodoItem"

const TodoList = ({todos, toggleComplete, deleteTodo}) => {

    const printTodos = (isCompleted) => {
        todos.map((todo) => {
                if (todo.completed === isCompleted) {
                    <TodoItem
                    key={todo.id}
                    todo = {todo}
                    toggleComplete = {toggleComplete}
                    todoDelete = { deleteTodo }
                    />
                }
        })
    }

    const printAllTodos = () => {
        printTodos(false);
        printTodos(true);
    }
    return <div className="todo-list">
        {   todos.length === 0 ? <h2 className="todo-list__label">Задач нет</h2> : printAllTodos()
        }
    </div>
}

export default TodoList;
