import AddTodoForm from "./components/forms/AddTodoForm";
import TodoList from "./components/core/TodoList";

import { useState, useEffect } from "react";

function App() {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState('all');

  useEffect(()=>(getTodosFromStorage()),[]);
  useEffect(()=>(setTodosToStorage),[todos]);


  const addTodo = (todoItemText) => {
    setTodos([
      ...todos,
      {
        id: Date.now(),
        text: todoItemText,
        complited: false,
      }
    ])
  }

  const toggleComplite = (id) => {
    setTodos(todos.map((todo)=>{
      if (todo.id === id) todo.complited = !todo.complited
    }))
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id))
  }

  const getTodosFromStorage = () => {
    const todosFromStorage = localStorage.getItem('todos');
    if (todosFromStorage) setTodos(JSON.parse(todosFromStorage))
  }

  const setTodosToStorage = () => {
    const todosJSON = JSON.stringify(todos);
    localStorage.setItem('todos', todosJSON);
  }

  return (
    <div className="App">
      <AddTodoForm addTodo = {addTodo} />
      <TodoList todos = {todos} toggleComplite = {toggleComplite} deleteTodo = {deleteTodo} />
    </div>
  );
}

export default App;
