const TodoItem = ({todo, toggleComplete, todoDelete}) => {
    return <div className={`todo-item ${todo.completed ? 'completed' : ''}`}>
      <input 
        type="checkbox" 
        checked={todo.completed} 
        onChange={() => toggleComplete(todo.id)}
      />
      <span>{todo.text}</span>
      <button onClick={() => todoDelete(todo.id)}>Delete</button>
    </div>
}

export default TodoItem;
